<?php

namespace App\Http\Controllers;

use Illuminate\Http\Request;

class PhotoController extends Controller
{
    
   // Declare $photos as a class property
    protected $photos = [];

    public function __construct()
    {
        // Initialize photos array in the constructor
        $this->photos = [
            [
                'id' => 1,
                'title' => 'Wedding Photoshoot',
                'description' => 'Beautiful wedding photography.',
                'image_url' => 'data:image/jpeg;base64,'
            ],
            [
                'id' => 2,
                'title' => 'Studio Portraits',
                'description' => 'Professional studio portraits.',
                'image_url' => 'data:image/jpeg;base64,'
            ]
        ];
    }

    // Index function to return all photos as JSON
    public function index()
    {
        return response()->json($this->photos);
    }

    // Store function to simulate photo upload
    public function store(Request $request)
    {
        return response()->json(['message' => 'Photo uploaded successfully'], 201);
    }
}
